
public class Ex3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			
	          System.out.println(sum(1,10));   
	          System.out.println(sum(20,30));
	          System.out.println(sum(35,45));
	

	}

          public static int sum(int m,int n) {
              
        	  int sum=0;
        
        	  	for(int i=m;i<=n;i++) {
        	
        	  		sum+=i;
        	  
        		
        	  	}
        	  	return sum;
          }






}
